xpath.script:
function on() {
document.getElementById("overlay").classList.add("d-flex");
document.getElementById("overlay").style.display = "block";
document.getElementById("overlay").style.transition = "all 1s";
document.getElementById("cancel_search_btn").style.display = "block";
document.getElementById("mager_search").style.color = "white";


}

function off() {
document.getElementById("overlay").classList.remove("d-flex");
document.getElementById("overlay").style.display = "none";
document.getElementById("cancel_search_btn").style.display = "none";
document.getElementById("search_bar").style.fontSize = "1rem";
document.getElementById("mager_search").style.color = "black";
}

function changeFontSize(media_query) {
if (media_query.matches) {
document.getElementById("mager_search").style.fontSize = "1.5rem";
document.getElementById("search_bar").style.fontSize = "1.5rem";
document.getElementById("trash_icon").style.fontSize = "1.5rem";
} else {
document.getElementById("mager_search").style.fontSize = "3rem";
document.getElementById("search_bar").style.fontSize = "3rem";
document.getElementById("trash_icon").style.fontSize = "3rem";
}
}
var media_query = window.matchMedia("(max-width: 1050px)")
changeFontSize(media_query) // Call listener function at run time
media_query.addListener(changeFontSize) // Attach listener function on state changes
